def steps(diagram)
   # find the entry point (from top)
  x = diagram[0].index('|') # x-axis
  pos = [0,x] # initial position
  
  d = [1,0] # direction; going down
  n = diagram.size # dimensionality
  z = [[-1,0],[0,1],[1,0],[0,-1]] # top, right, bottom, left

  letters = []
  s = 0

  # ---

  loop do
    break if pos[0] < 0 || pos[0] > (n-1)
    g = diagram[pos[0]][pos[1]] # get diagram element
    break if g == ' ' # nothing?
    
    if g == '+'
      # first
      d = z.find do |y,x|
        next if d == [-y,-x] # ignore the direction we came from
        v = [pos[0]+y, pos[1]+x]
        next if v[0] < 0 || v[0] > (n-1) 
        (diagram[v[0]][v[1]] || ' ') != ' '
      end
    end
    
    # continue in the same direction
    # update pos
    s += 1
    pos = pos.zip(d).map { |y,x| y+x }  
  end
  
  # number of steps
  s
end

